function getSkillDescription(skill) {
	var skillID = skill.skillId;
	var refID = skill.refId;
	var rawDescription = getRawSkillDescription(refID);
	genieStat = genieStats[skillID];
	for (var i = 0; i < genieStat.length; i++) {
		if (genieStat[i] != null) {
			var statString = "" + genieStat[i][0] + ((genieStat[i].length > 1) ?" - " + genieStat[i][genieStat[i].length - 1] : "");
			var keyString = "%%" + i + "%%";
			rawDescription = rawDescription.replace(keyString, statString);
		}
	}
	return rawDescription;
}
